package basic;

public class AssignmentDemo
{
    public static void main(String[] args)
    {
        // TODO Auto-generated method stub
        int x = 3, y = 2;

        x += y; //x = x + y = 5
        System.out.println("X = " + x);

        x -= y; //x = x - y = 3
        System.out.println("X = " + x);

        x *= y; //x = x * y = 6
        System.out.println("X = " + x);

        x /= y; //x = x / y = 3
        System.out.println("X = " + x);

        x %= y; //x = x % y = 1
        System.out.println("X = " + x);

    }
}

